; function that calls a function to do fly away or teleport away graphics
DoFlyOrTeleportAwayGraphics: ; 098f (0:098f)
	ld b, BANK(_DoFlyOrTeleportAwayGraphics)
	ld hl, _DoFlyOrTeleportAwayGraphics
